# Grab the company names from the HTML using regular expressions, and output them to text files

ack -w h3 forbes* | perl -ne 'if (/>([^\<]+)<\/h3>/) { print "$+\r\n"; } '  > forbes-global-2000-companyies.txt 
ack --literal '/company/view/' fortune* | perl -ne 'if (/>([^<]+)<\/a>/) { print "$+\n"; } ' > fortune-companyies-1000.txt
ack --literal 'http://www.inc.com/inc5000/profile/' * | perl -ne 'if (/>([^<]+)<\/a>/) { print "$+\n"; } ' > inc-top5000.txt
